-- FOS01 凭証（仕訳リスト xls 2025年3-12月 から生成）
-- 全 INSERT は FOS01 のみ。他社データへの影響なし。
BEGIN;


-- voucher_sequences を最大連番に更新 (FOS01 のみ)
DO $$
DECLARE r RECORD;
BEGIN
  FOR r IN
    SELECT substring(voucher_no FROM 1 FOR 4) AS yymm,
           max(substring(voucher_no FROM 5)::int) AS max_no
    FROM vouchers
    WHERE company_code = 'FOS01'
      AND voucher_no ~ '^[0-9]{4}[0-9]+$'
    GROUP BY 1
  LOOP
    VALUES ('FOS01', r.yymm, r.max_no)
    ON CONFLICT (company_code, yymm) DO UPDATE
      SET last_number = GREATEST(voucher_sequences.last_number, EXCLUDED.last_number);
    RAISE NOTICE 'FOS01/% sequence → %', r.yymm, r.max_no;
  END LOOP;
END $$;

COMMIT;