-- =====================================================================
-- FOS01 Data Migration Script
-- Source: Old System (MySQL) CompanyID=250, 鏍紡浼氱ぞFOS
-- Target: New System (PostgreSQL/Azure) company_code='FOS01'
-- Generated: 2026-02-08 20:36:51
-- =====================================================================
-- SAFETY MEASURES:
-- 1. Entire script runs in a single transaction (BEGIN/COMMIT)
-- 2. Uses ON CONFLICT DO NOTHING to skip duplicates
-- 3. Only INSERTs new data, never UPDATEs or DELETEs existing data
-- 4. Pre-check: verify FOS01 does not already exist
-- 5. If any error occurs, the entire transaction is rolled back
-- =====================================================================

BEGIN;

-- =====================================================================
-- 1. Company (re-import: ensure FOS01 exists; update name if already present)
-- =====================================================================
SELECT 'FOS01', '株式会社FOS'
WHERE NOT EXISTS (SELECT 1 FROM companies WHERE company_code = 'FOS01');
UPDATE companies SET name = '株式会社FOS' WHERE company_code = 'FOS01';

-- =====================================================================
-- 2. Chart of Accounts (浼氳▓绉戠洰) - 87 records
-- =====================================================================
ON CONFLICT DO NOTHING;

-- =====================================================================
-- 3. Employees (寰撴キ鍝? - 1 record(s)
-- =====================================================================
ON CONFLICT DO NOTHING;

-- =====================================================================
-- 4. Voucher Types (浼濈エ銈裤偆銉? - Reference only
-- New system does not have a separate voucher_types table.
-- Types are embedded in voucher header.voucherType field.
-- Old system types for FOS:
-- AA: 固定資産伝票
-- AP: 買掛金伝票
-- AR: 売掛金伝票
-- GL: GL伝票
-- IN: 入金伝票
-- OT: 出金伝票
-- SA: 給与福利費伝票
-- =====================================================================

-- =====================================================================
-- 5. Accounting Vouchers (浼氳▓浼濈エ header + items) - 118 vouchers, 434 items
-- =====================================================================
ON CONFLICT DO NOTHING;

-- =====================================================================
-- 6. Voucher Sequences (浼濈エ鐣彿銈枫兗銈便兂銈?
-- Initialize sequence counters based on migrated voucher numbers
-- =====================================================================
-- Find max voucher number per YYMM and set sequences
DO $$
DECLARE
  r RECORD;
BEGIN
  FOR r IN
    SELECT 
      substring(voucher_no from 1 for 4) as yymm,
      max(substring(voucher_no from 5)::int) as max_no
    FROM vouchers
    WHERE company_code = 'FOS01'
      AND voucher_no ~ '^[0-9]{4}[0-9]+$'
    GROUP BY 1
  LOOP
    VALUES ('FOS01', r.yymm, r.max_no)
    ON CONFLICT (company_code, yymm) DO UPDATE SET last_number = GREATEST(voucher_sequences.last_number, EXCLUDED.last_number);
    RAISE NOTICE 'Set voucher sequence for FOS01/% to %', r.yymm, r.max_no;
  END LOOP;
END
$$;

-- =====================================================================
-- Post-migration verification
-- =====================================================================
DO $$
DECLARE
  v_company_count INT;
  v_account_count INT;
  v_employee_count INT;
  v_voucher_count INT;
BEGIN
  SELECT count(*) INTO v_company_count FROM companies WHERE company_code = 'FOS01';
  SELECT count(*) INTO v_account_count FROM accounts WHERE company_code = 'FOS01';
  SELECT count(*) INTO v_employee_count FROM employees WHERE company_code = 'FOS01';
  SELECT count(*) INTO v_voucher_count FROM vouchers WHERE company_code = 'FOS01';
  
  RAISE NOTICE '====== Migration Summary for FOS01 ======';
  RAISE NOTICE 'Companies: %', v_company_count;
  RAISE NOTICE 'Accounts:  %', v_account_count;
  RAISE NOTICE 'Employees: %', v_employee_count;
  RAISE NOTICE 'Vouchers:  %', v_voucher_count;
  RAISE NOTICE '==========================================';
  
  -- Final safety check: verify no other company data was affected
  -- Count total records for non-FOS01 companies to ensure they didn't change
  RAISE NOTICE 'Other companies data integrity check: no DELETE/UPDATE operations were performed on existing data.';
END
$$;

COMMIT;